// src/pages/Opportunities.js
import React from 'react';
import Slider from 'react-slick';
import './Opportunities.css';

function Opportunities() {
  // Slider settings for the carousel
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
  };

  return (
    <>
      {/* Spacer div to push content below the header */}
      <div style={{ height: '5rem' }}></div>
      
      {/* Image Carousel */}
      <Slider {...settings} className="carousel">
        <div className="carousel-image">
          <a href="#opportunity1"><img src="/assets/image1.png" alt="Opportunity 1" /></a>
        </div>
        <div className="carousel-image">
          <a href="#opportunity2"><img src="/assets/image2.png" alt="Opportunity 2" /></a>
        </div>
        <div className="carousel-image">
          <a href="#opportunity3"><img src="/assets/image3.png" alt="Opportunity 3" /></a>
        </div>
      </Slider>

      {/* Introductory Text */}
      <div className="intro-text">
        <h2>Explore Our Opportunities</h2>
        <p>
          We offer a variety of opportunities to help you grow, learn, test and make an impact. 
          Scroll down to learn more about each one and find the right fit for you.
        </p>
      </div>

      {/* Opportunity Sections */}
      <div id="opportunity1" className="opportunity-section">
        <h3>Opportunity 1</h3>
        <p>Description of Opportunity 1...</p>
      </div>

      <div id="opportunity2" className="opportunity-section">
        <h3>Opportunity 2</h3>
        <p>Description of Opportunity 2...</p>
      </div>

      <div id="opportunity3" className="opportunity-section">
        <h3>Opportunity 3</h3>
        <p>Description of Opportunity 3...</p>
      </div>
    </>
  );
}

export default Opportunities;
