// src/components/Home/Events.js
import React, { useState, useEffect, useRef } from 'react';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import './Events.css';
import eventTemplate1 from '../../assets/event_template1.png';
import eventTemplate2 from '../../assets/event_template2.png';

function Events() {
  const eventDescriptions = [
    "Event 1: Join us for an exciting workshop on creative writing techniques!",
    "Event 2: Don't miss our annual storytelling event where we celebrate narratives from around the world!",
  ];

  const [currentEventIndex, setCurrentEventIndex] = useState(0);
  const descriptionRef = useRef(null);
  const [descriptionHeight, setDescriptionHeight] = useState(0);

  useEffect(() => {
    // Calculate the max height based on the longest description
    const maxHeight = Math.max(...eventDescriptions.map(desc => {
      const tempDiv = document.createElement("div");
      tempDiv.style.position = "absolute";
      tempDiv.style.visibility = "hidden";
      tempDiv.style.width = "100%";
      tempDiv.style.fontSize = "1.25rem"; // Match description font size
      tempDiv.innerText = desc;
      document.body.appendChild(tempDiv);
      const height = tempDiv.offsetHeight;
      document.body.removeChild(tempDiv);
      return height;
    }));
    setDescriptionHeight(maxHeight);
  }, []);

  const handleCarouselChange = (index) => {
    setCurrentEventIndex(index);
  };

  return (
    <div className="events">
      <h2>Events</h2>
      <p>Check out some of our latest events and activities!</p>
      <div className="events-content">
        <div className="events-carousel">
          <Carousel showThumbs={false} infiniteLoop autoPlay onChange={handleCarouselChange}>
            <div>
              <img src={eventTemplate1} alt="Event Template 1" />
            </div>
            <div>
              <img src={eventTemplate2} alt="Event Template 2" />
            </div>
          </Carousel>
        </div>
        <div
          className="event-description"
          ref={descriptionRef}
          style={{ minHeight: `${descriptionHeight}px` }}
        >
          <p>{eventDescriptions[currentEventIndex]}</p>
        </div>
      </div>
    </div>
  );
}

export default Events;
