// App.js
import React, { useState, useRef, useEffect } from 'react';
import { Route, Routes, useLocation } from 'react-router-dom';
import './App.css';
import Header from './components/Header';
import Footer from './components/Footer';
import Parallax from './components/Parallax';
import Welcome from './components/Home/Welcome';
import Sponsorships from './components/Home/Sponsorships';
import Events from './components/Home/Events';
import AboutUs from './pages/AboutUs';
import EventsPage from './pages/EventsPage';
import Loading from './components/Loading';
import ScrollToTop from './components/ScrollToTop';

function App() {
  const [loading, setLoading] = useState(true); // Set to true initially
  const location = useLocation();
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const requestRef = useRef(null);

  const updateMousePosition = (e) => {
    const { clientX: x, clientY: y } = e;
    cancelAnimationFrame(requestRef.current);
    requestRef.current = requestAnimationFrame(() => {
      setMousePosition({ x, y });
    });
  };

  useEffect(() => {
    window.addEventListener("mousemove", updateMousePosition);

    return () => {
      window.removeEventListener("mousemove", updateMousePosition);
      cancelAnimationFrame(requestRef.current);
    };
  }, []);

  // Handle initial load
  useEffect(() => {
    const initialLoadTimeout = setTimeout(() => setLoading(false), 1000); // Adjust time if necessary

    return () => clearTimeout(initialLoadTimeout);
  }, []);

  // Show loading screen during route changes
  useEffect(() => {
    setLoading(true); // Show loading screen when location changes
    const routeChangeTimeout = setTimeout(() => setLoading(false), 1000); // Adjust as needed

    return () => clearTimeout(routeChangeTimeout);
  }, [location]);

  return (
    <div className="App">
      {loading && <Loading />}
      <Parallax mousePosition={mousePosition} />
      <Header />

      <ScrollToTop /> {/* Ensures page starts at top on route change */}
      <div className="content">
        <Routes>
          <Route path="/" element={<><Welcome /><Events /><Sponsorships /></>} />
          <Route path="/about-us" element={<AboutUs />} />
          <Route path="/events" element={<EventsPage />} />
        </Routes>
      </div>

      <Footer />
    </div>
  );
}

export default App;
