// App.js
import React, { useState, useEffect, useRef, Suspense } from 'react';
import { Route, Routes, useLocation } from 'react-router-dom';
import './App.css';
import Header from './components/Header';
import Footer from './components/Footer';
import Parallax from './components/Parallax';
import Welcome from './components/Home/Welcome';
import Sponsorships from './components/Home/Sponsorships';
import Events from './components/Home/Events';
import AboutUs from './pages/AboutUs';
import EventsPage from './pages/EventsPage';
import Loading from './components/Loading';
import ScrollToTop from './components/ScrollToTop';

function App() {
  const [showLoading, setShowLoading] = useState(true);
  const [showContent, setShowContent] = useState(false);
  const location = useLocation();
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const requestRef = useRef(null);

  const updateMousePosition = (e) => {
    const { clientX: x, clientY: y } = e;
    cancelAnimationFrame(requestRef.current);
    requestRef.current = requestAnimationFrame(() => {
      setMousePosition({ x, y });
    });
  };

  useEffect(() => {
    window.addEventListener("mousemove", updateMousePosition);
    return () => {
      window.removeEventListener("mousemove", updateMousePosition);
      cancelAnimationFrame(requestRef.current);
    };
  }, []);

  useEffect(() => {
    // When route changes:
    // 1. Hide the current content immediately
    setShowContent(false);
    // 2. Show loading screen
    setShowLoading(true);

    // 3. After a brief delay, begin loading the new content
    const loadContentTimer = setTimeout(() => {
      setShowContent(true);
      // 4. Once content is loaded, fade out the loading screen
      const hideLoadingTimer = setTimeout(() => {
        setShowLoading(false);
      }, 1000); // Adjust this delay as needed

      return () => clearTimeout(hideLoadingTimer);
    }, 100); // Short delay to ensure loading screen is visible first

    return () => clearTimeout(loadContentTimer);
  }, [location]);

  const currentRouteContent = (
    <>
      <ScrollToTop />
      <Parallax mousePosition={mousePosition} />
      <Header />
      <div>
        <Routes location={location}>
          <Route path="/" element={<><Welcome /><Events /><Sponsorships /></>} />
          <Route path="/about-us" element={<AboutUs />} />
          <Route path="/events" element={<EventsPage />} />
        </Routes>
      </div>
      <Footer />
    </>
  );

  return (
    <div className="App">
      {showLoading && <Loading />}
      <div 
        className={`content ${showContent ? 'loaded' : ''}`}
        style={{ 
          display: showContent ? 'block' : 'none' // Use display instead of visibility
        }}
      >
        {showContent && currentRouteContent}
      </div>
    </div>
  );
}

export default App;
