// src/pages/EventsPage.js
import React from 'react';
import './EventsPage.css';
import OurEventsTitle from '../assets/OurEventsTitle.png';
import eventTemplate1 from '../assets/event_template1.png';
import eventTemplate2 from '../assets/event_template2.png';
import eventTemplate3 from '../assets/event_template3.png';
import eventTemplate4 from '../assets/event_template4.png';

function EventsPage() {
  const events = [
    { id: 'event-1', image: eventTemplate1, title: "Mobile Development Workshop", description: "Learn the fundamentals of mobile app development." },
    { id: 'event-2', image: eventTemplate2, title: "Peer Mentor Recruitment", description: "Join us as a mentor to guide newcomers!" },
    { id: 'event-3', image: eventTemplate3, title: "Arista Barbecue", description: "Enjoy a fun barbecue event with peers." },
    { id: 'event-4', image: eventTemplate4, title: "Exciting Event", description: "Join us for another exciting event." },
  ];

  return (
    <div className="events-page">
      {/* Replace the heading with the OurEventsTitle image */}
      <img src={OurEventsTitle} alt="Our Events" className="events-title-image" />
      <div className="events-list">
        {events.map((event) => (
          <div className="event-box" key={event.id} id={event.id}>
            <img src={event.image} alt={event.title} className="event-image" />
            <div className="event-info">
              <h3>{event.title}</h3>
              <p>{event.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default EventsPage;
