// src/components/Home/Events.js
import React, { useEffect, useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Events.css';
import eventTemplate1 from '../../assets/event_template1.png';
import eventTemplate2 from '../../assets/event_template2.png';
import eventTemplate3 from '../../assets/event_template3.png';
import eventTemplate4 from '../../assets/event_template4.png';

function Events() {
  const navigate = useNavigate();
  const trackRef = useRef(null);
  const [offset, setOffset] = useState(0);

  // Define the events array
  const events = [
    { id: 'event-1', image: eventTemplate1, description: "Event 1: Mobile Development Workshop" },
    { id: 'event-2', image: eventTemplate2, description: "Event 2: Peer Mentor Recruitment" },
    { id: 'event-3', image: eventTemplate3, description: "Event 3: Arista Barbecue" },
    { id: 'event-4', image: eventTemplate4, description: "Event 4: Another Exciting Event" },
  ];

  const handleClick = (eventId) => {
    navigate(`/events#${eventId}`);
  };

  useEffect(() => {
    const scroll = () => {
      setOffset((prevOffset) => {
        const newOffset = prevOffset - 1; // Adjust speed here
        const trackWidth = trackRef.current.scrollWidth / 2; // Width of one set of items

        // Reset the scroll offset when it reaches half the track width
        if (Math.abs(newOffset) >= trackWidth) {
          return 0;
        }
        return newOffset;
      });

      requestAnimationFrame(scroll);
    };

    const animationFrame = requestAnimationFrame(scroll);
    return () => cancelAnimationFrame(animationFrame); // Cleanup on unmount
  }, []);

  return (
    <div className="events">
      <h2>Events</h2>
      <p>Click on the Cover Photo for more info!</p>
      <div className="carousel-wrapper">
        <div
          className="carousel-track"
          ref={trackRef}
          style={{ transform: `translateX(${offset}px)` }}
        >
          {/* Render two sets of images to create the infinite scroll effect */}
          {[...events, ...events].map((event, index) => (
            <div
              className="carousel-item"
              key={`${event.id}-${index}`} // Unique key for each item
              onClick={() => handleClick(event.id)}
            >
              <img src={event.image} alt={event.description} />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Events;
