// src/components/Welcome.js
import React, { useState } from 'react';
import './Welcome.css';
import backgroundImage from '../assets/night-sky.jpg'; // Adjust the path as needed

function Welcome() {
  const [position, setPosition] = useState({ x: 0, y: 0 });

  // Event handler to update background position based on mouse movement
  const handleMouseMove = (e) => {
    const { innerWidth, innerHeight } = window;
    const x = (e.clientX / innerWidth) * 100; // Normalize x position
    const y = (e.clientY / innerHeight) * 100; // Normalize y position
    setPosition({ x, y });
  };

  return (
    <section
      className="welcome-section"
      style={{
        backgroundImage: `url(${backgroundImage})`,
        backgroundPosition: `${position.x}% ${position.y}%`,
      }}
      onMouseMove={handleMouseMove}
    >
      <h2>WELCOME</h2>
      <p>
        UNSW PsychSoc is the constituent society for UNSW Psychology students or anyone interested in psychology.
      </p>
      <p>
        We aim to engage, support, and connect all students within the UNSW Psychology community.
      </p>
    </section>
  );
}

export default Welcome;
