// src/components/Home/Events.js
import React, { useState } from 'react';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css'; // Import carousel styles
import './Events.css';
import eventTemplate1 from '../../assets/event_template1.png';
import eventTemplate2 from '../../assets/event_template2.png';

function Events() {
  const eventDescriptions = [
    "Join us for an exciting workshop on creative writing techniques!",
    "Don't miss our annual storytelling event where we celebrate narratives from around the world!",
  ];

  const [currentEventIndex, setCurrentEventIndex] = useState(0);

  const handleCarouselChange = (index) => {
    setCurrentEventIndex(index);
  };

  return (
    <div className="events">
      <h2>Future Events</h2>
      <p>Check out some of our latest events and activities!</p>
      <div className="events-content">
        <div className="events-carousel">
          <Carousel showThumbs={false} infiniteLoop autoPlay onChange={handleCarouselChange}>
            <div>
              <img src={eventTemplate1} alt="Event Template 1" />
            </div>
            <div>
              <img src={eventTemplate2} alt="Event Template 2" />
            </div>
          </Carousel>
        </div>
        <div className="event-description">
          <p>{eventDescriptions[currentEventIndex]}</p>
        </div>
      </div>
    </div>
  );
}

export default Events;
