// src/components/Home/Events.js
import React, { useState, useEffect } from 'react';
import './Events.css';
import eventTemplate1 from '../../assets/event_template1.png';
import eventTemplate2 from '../../assets/event_template2.png';
import eventTemplate3 from '../../assets/event_template3.png';

function Events() {
  const events = [
    { image: eventTemplate1, description: "Event 1: Join us for an exciting workshop on creative writing techniques!" },
    { image: eventTemplate2, description: "Event 2: Don't miss our annual storytelling event where we celebrate narratives from around the world!" },
    { image: eventTemplate3, description: "Event 3: A networking event for creative professionals to connect and grow." },
  ];

  const [currentIndex, setCurrentIndex] = useState(0);
  const [transitionDirection, setTransitionDirection] = useState(""); // Track transition direction

  const handleNext = () => {
    setTransitionDirection("next");
    setCurrentIndex((prevIndex) => (prevIndex + 1) % events.length);
  };

  const handlePrev = () => {
    setTransitionDirection("prev");
    setCurrentIndex((prevIndex) => (prevIndex - 1 + events.length) % events.length);
  };

  useEffect(() => {
    // Reset transition direction after the animation completes
    const timer = setTimeout(() => setTransitionDirection(""), 500); // match transition duration in CSS
    return () => clearTimeout(timer);
  }, [currentIndex]);

  const prevIndex = (currentIndex - 1 + events.length) % events.length;
  const nextIndex = (currentIndex + 1) % events.length;

  return (
    <div className="events">
      <h2>Events</h2>
      <p>Check out some of our latest events and activities!</p>
      <div className="events-content">
        <div className="carousel-wrapper">
          <div className={`carousel-preview left-preview ${transitionDirection === "prev" ? "slide-in" : ""}`} onClick={handlePrev}>
            <img src={events[prevIndex].image} alt="Previous Event" />
          </div>
          <div className={`carousel-main ${transitionDirection}`}>
            <img src={events[currentIndex].image} alt="Current Event" />
          </div>
          <div className={`carousel-preview right-preview ${transitionDirection === "next" ? "slide-in" : ""}`} onClick={handleNext}>
            <img src={events[nextIndex].image} alt="Next Event" />
          </div>
        </div>
        <div className="event-description">
          <p>{events[currentIndex].description}</p>
        </div>
      </div>
    </div>
  );
}

export default Events;
