// src/pages/Events.js
import React from 'react';
import './Events.css';
import eventTemplate1 from '../assets/event_template1.png';
import eventTemplate2 from '../assets/event_template2.png';
import eventTemplate3 from '../assets/event_template3.png';
import eventTemplate4 from '../assets/event_template4.png';

function Events() {
  const events = [
    { image: eventTemplate1, title: "Mobile Development Workshop", description: "Learn the fundamentals of mobile app development." },
    { image: eventTemplate2, title: "Peer Mentor Recruitment", description: "Join us as a mentor to guide newcomers!" },
    { image: eventTemplate3, title: "Arista Barbecue", description: "Enjoy a fun barbecue event with peers." },
    { image: eventTemplate4, title: "Exciting Event", description: "Join us for another exciting event." },
  ];

  return (
    <div className="events-page">
      <h2>Our Events</h2>
      <div className="events-list">
        {events.map((event, index) => (
          <div className="event-box" key={index}>
            <a href={`#event-${index}`}>
              <img src={event.image} alt={event.title} className="event-image" />
            </a>
            <div className="event-info" id={`event-${index}`}>
              <h3>{event.title}</h3>
              <p>{event.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default EventsPage;
