// App.js
import React, { useState, useEffect, useRef } from 'react';
import { Route, Routes, useLocation } from 'react-router-dom';
import './App.css';
import Loading from './components/Loading';
import ScrollToTop from './components/ScrollToTop';
import Parallax from './components/Parallax';
import Header from './components/Header';
import Footer from './components/Footer';
import Welcome from './components/Home/Welcome';
import Sponsorships from './components/Home/Sponsorships';
import Events from './components/Home/Events';
import AboutUs from './pages/AboutUs';
import EventsPage from './pages/EventsPage';

function App() {
  const [loading, setLoading] = useState(true);
  const location = useLocation();

  useEffect(() => {
    // Trigger loading on route change
    setLoading(true);
  }, [location]);

  return (
    <div className="App">
      {loading && <Loading onComplete={() => setLoading(false)} />} {/* Pass onComplete prop */}
      <div className={`black-overlay ${loading ? 'visible' : ''}`}></div>

      {!loading && (
        <>
          <ScrollToTop />
          <Parallax />
          <Header />
          <div className="content">
            <Routes>
              <Route path="/" element={<><Welcome /><Events /><Sponsorships /></>} />
              <Route path="/about-us" element={<AboutUs />} />
              <Route path="/events" element={<EventsPage />} />
            </Routes>
          </div>
          <Footer />
        </>
      )}
    </div>
  );
}

export default App;
