// src/components/Welcome.js
import React, { useState } from 'react';
import './Welcome.css';
import backgroundImage from '../assets/night-sky.svg'; // Adjust path as needed

function Welcome() {
  const [position, setPosition] = useState({ x: 50, y: 50 });

  const handleMouseMove = (e) => {
    const { innerWidth, innerHeight } = window;
    const x = 50 + ((e.clientX / innerWidth) - 0.5) * 20;
    const y = 50 + ((e.clientY / innerHeight) - 0.5) * 20;
    setPosition({ x, y });
  };

  return (
    <section
      className="welcome-section"
      style={{
        backgroundImage: `url(${backgroundImage})`,
        backgroundPosition: `${position.x}% ${position.y}%`,
      }}
      onMouseMove={handleMouseMove}
    >
      <p className="intro-text">Hey! We are UNSW's</p>
      <h2>ENGLISH & CREATIVE WRITING SOCIETY</h2>

      {/* Wave Divider at the Bottom of Welcome Section */}
      <div className="wave-divider">
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1440 320">
          <path fill="#000" fillOpacity="1" d="M0,224L48,192C96,160,192,96,288,96C384,96,480,160,576,181.3C672,203,768,181,864,170.7C960,160,1056,160,1152,160C1248,160,1344,160,1392,160L1440,160V320H0V224Z"></path>
        </svg>
      </div>
    </section>
  );
}

export default Welcome;
