// AboutUs.js
import React, { useState, useEffect } from 'react';
import './AboutUs.css';
import AboutUsTitle from '../assets/AboutUsTitle.png';
import member1 from '../assets/member1.jpg';
import member2 from '../assets/member2.jpg';
import member3 from '../assets/member3.jpg';
import member4 from '../assets/member4.jpg';

function AboutUs() {
  const [blackout, setBlackout] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => setBlackout(false), 500); // 0.5-second blackout
    return () => clearTimeout(timer);
  }, []);

  return (
    <>
      {blackout && <div className="blackout-overlay"></div>}
      <div className="about-us-container">
        <img src={AboutUsTitle} alt="About Us" className="about-us-title-image" />
        
        <section className="about-us-section">
          <h2 className="about-us-heading">Who We Are</h2>
          <p className="about-us-text">
            We are the Software Development Society, a place for imaginative and inventive students...
          </p>
        </section>

        <section className="about-us-section">
          <h2 className="about-us-heading">Our Mission</h2>
          <p className="about-us-text">
            Our primary goal is to develop solutions that enhance the lives of university students...
          </p>
        </section>

        <section className="about-us-section team-section">
          <h2 className="about-us-heading">The Team</h2>
          <div className="team-grid">
            <div className="team-member">
              <img src={member1} alt="Team Member 1" className="team-photo" />
              <p className="team-name">Alex Johnson (President)</p>
            </div>
            <div className="team-member">
              <img src={member2} alt="Team Member 2" className="team-photo" />
              <p className="team-name">Taylor Kim (Secretary)</p>
            </div>
            <div className="team-member">
              <img src={member3} alt="Team Member 3" className="team-photo" />
              <p className="team-name">Jordan Lee (Treasurer)</p>
            </div>
            <div className="team-member">
              <img src={member4} alt="Team Member 4" className="team-photo" />
              <p className="team-name">Casey Smith (Marketing)</p>
            </div>
          </div>
        </section>
      </div>
    </>
  );
}

export default AboutUs;
