// src/components/Footer.js
import React, { useState } from 'react';
import './Footer.css';
import emailIcon from '../assets/email.png'; // Replace with your actual paths
import instagramIcon from '../assets/instagram.png';
import discordIcon from '../assets/discord.png';
import otherIcon from '../assets/other.png'; // Add a fourth communication method if needed

function Footer() {
  const [activeIcon, setActiveIcon] = useState(null);

  const handleIconClick = (icon) => {
    setActiveIcon(icon);
    if (icon === 'email') {
      navigator.clipboard.writeText('unswpsychsoc@example.com');
      alert('Email copied to clipboard!');
    }
  };

  return (
    <footer className="footer">
      <div className="footer-content">
        <b> Contact Us! </b>
        <div className="social-links">
          <img
            src={emailIcon}
            alt="Email"
            className={`social-icon ${activeIcon === 'email' ? 'active' : ''}`}
            onClick={() => handleIconClick('email')}
          />
          <a href="https://www.instagram.com/unswecwsoc/" target="_blank" rel="noopener noreferrer">
            <img
              src={instagramIcon}
              alt="Instagram"
              className={`social-icon ${activeIcon === 'instagram' ? 'active' : ''}`}
              onClick={() => setActiveIcon('instagram')}
            />
          </a>
          <a href="https://discord.gg/HBrJz7Fh" target="_blank" rel="noopener noreferrer">
            <img
              src={discordIcon}
              alt="Discord"
              className={`social-icon ${activeIcon === 'discord' ? 'active' : ''}`}
              onClick={() => setActiveIcon('discord')}
            />
          </a>
          <img
            src={otherIcon}
            alt="Other"
            className={`social-icon ${activeIcon === 'other' ? 'active' : ''}`}
            onClick={() => setActiveIcon('other')}
          />
        </div>
      </div>
    </footer>
  );
}

export default Footer;
