// src/components/Home/Events.js
import React from 'react';
import { Link } from 'react-router-dom'; // Import Link for internal navigation
import './Events.css';
import eventTemplate1 from '../../assets/event_template1.png';
import eventTemplate2 from '../../assets/event_template2.png';
import eventTemplate3 from '../../assets/event_template3.png';
import eventTemplate4 from '../../assets/event_template4.png';

function Events() {
  const events = [
    { image: eventTemplate1, description: "Event 1: Mobile Development Workshop", link: "/events#event-1" },
    { image: eventTemplate2, description: "Event 2: Peer Mentor Recruitment", link: "/events#event-2" },
    { image: eventTemplate3, description: "Event 3: Arista Barbecue", link: "/events#event-3" },
    { image: eventTemplate4, description: "Event 4: Another Exciting Event", link: "/events#event-4" },
  ];

  return (
    <div className="events">
      <h2>Events</h2>
      <p>Check out some of our latest events and activities!</p>
      <div className="carousel-wrapper">
        <div className="carousel-track">
          {events.map((event, index) => (
            <div className="carousel-item" key={index}>
              <Link to={event.link}>
                <img src={event.image} alt={`Event ${index + 1}`} />
              </Link>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Events;
