// src/components/Home/Events.js
import React, { useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Events.css';
import eventTemplate1 from '../../assets/event_template1.png';
import eventTemplate2 from '../../assets/event_template2.png';
import eventTemplate3 from '../../assets/event_template3.png';
import eventTemplate4 from '../../assets/event_template4.png';

function Events() {
  const navigate = useNavigate();
  const carouselRef = useRef(null);

  const events = [
    { id: 'event-1', image: eventTemplate1, description: "Event 1: Mobile Development Workshop" },
    { id: 'event-2', image: eventTemplate2, description: "Event 2: Peer Mentor Recruitment" },
    { id: 'event-3', image: eventTemplate3, description: "Event 3: Arista Barbecue" },
    { id: 'event-4', image: eventTemplate4, description: "Event 4: Another Exciting Event" },
  ];

  const handleClick = (eventId) => {
    navigate(`/events#${eventId}`);
  };

  useEffect(() => {
    const carousel = carouselRef.current;

    // Duplicate the items multiple times to create the effect
    const totalDuplicates = 3; // Increase if needed for smooth scrolling
    for (let i = 0; i < totalDuplicates; i++) {
      events.forEach((event) => {
        const item = document.createElement("div");
        item.className = "carousel-item";
        item.onclick = () => handleClick(event.id);
        const img = document.createElement("img");
        img.src = event.image;
        img.alt = event.description;
        item.appendChild(img);
        carousel.appendChild(item);
      });
    }

    // Set up animation for infinite scroll
    const scrollSpeed = 1; // Adjust speed
    let scrollPosition = 0;

    const animateScroll = () => {
      scrollPosition += scrollSpeed;
      if (scrollPosition >= carousel.scrollWidth / 2) {
        scrollPosition = 0; // Reset scroll when halfway through
      }
      carousel.scrollLeft = scrollPosition;
      requestAnimationFrame(animateScroll);
    };

    animateScroll();
  }, [events, handleClick]);

  return (
    <div className="events">
      <h2>Events</h2>
      <p>Click on the Cover Photo for more info!</p>
      <div className="carousel-wrapper" ref={carouselRef}>
        <div className="carousel-track">
          {events.map((event, index) => (
            <div className="carousel-item" key={event.id + index} onClick={() => handleClick(event.id)}>
              <img src={event.image} alt={event.description} />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Events;
