// src/components/Home/Events.js
import React, { useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './Events.css';
import eventTemplate1 from '../../assets/event_template1.png';
import eventTemplate2 from '../../assets/event_template2.png';
import eventTemplate3 from '../../assets/event_template3.png';
import eventTemplate4 from '../../assets/event_template4.png';

function Events() {
  const navigate = useNavigate();
  const trackRef = useRef(null);

  const events = [
    { id: 'event-1', image: eventTemplate1, description: "Event 1: Mobile Development Workshop" },
    { id: 'event-2', image: eventTemplate2, description: "Event 2: Peer Mentor Recruitment" },
    { id: 'event-3', image: eventTemplate3, description: "Event 3: Arista Barbecue" },
    { id: 'event-4', image: eventTemplate4, description: "Event 4: Another Exciting Event" },
  ];

  const handleClick = (eventId) => {
    navigate(`/events#${eventId}`);
  };

  useEffect(() => {
    const track = trackRef.current;
    const firstChild = track.firstElementChild;

    const scroll = () => {
      if (track.scrollLeft >= firstChild.clientWidth) {
        track.appendChild(firstChild); // Move the first item to the end
        track.scrollLeft -= firstChild.clientWidth; // Reset scroll position
      }
      track.scrollLeft += 1; // Adjust this value to control scroll speed
      requestAnimationFrame(scroll);
    };

    const animationFrame = requestAnimationFrame(scroll);
    return () => cancelAnimationFrame(animationFrame); // Clean up on component unmount
  }, []);

  return (
    <div className="events">
      <h2>Events</h2>
      <p>Click on the Cover Photo for more info!</p>
      <div className="carousel-wrapper" ref={trackRef}>
        <div className="carousel-track">
          {events.map((event, index) => (
            <div className="carousel-item" key={event.id} onClick={() => handleClick(event.id)}>
              <img src={event.image} alt={event.description} />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Events;
