// src/components/Home/Events.js
import React, { useState } from 'react';
import './Events.css';
import eventTemplate1 from '../../assets/event_template1.png';
import eventTemplate2 from '../../assets/event_template2.png';
import eventTemplate3 from '../../assets/event_template3.png';
import eventTemplate4 from '../../assets/event_template4.png';

function Events() {
  const events = [
    { image: eventTemplate1, description: "Event 1: Mobile Development Workshop" },
    { image: eventTemplate2, description: "Event 2: Peer Mentor Recruitment" },
    { image: eventTemplate3, description: "Event 3: Arista Barbecue" },
    { image: eventTemplate4, description: "Event 4: Another Exciting Event" },
  ];

  const [currentIndex, setCurrentIndex] = useState(0);
  const itemsToShow = 3; // Number of items to display at once

  const handleNext = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex + 1 < events.length - itemsToShow + 1 ? prevIndex + 1 : 0
    );
  };

  const handlePrev = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex - 1 >= 0 ? prevIndex - 1 : events.length - itemsToShow
    );
  };

  return (
    <div className="events">
      <h2>Events</h2>
      <p>Check out some of our latest events and activities!</p>
      <div className="carousel-wrapper">
        <button onClick={handlePrev} className="carousel-button">‹</button>
        <div className="carousel-container">
          <div
            className="carousel-track"
            style={{ transform: `translateX(-${currentIndex * (100 / itemsToShow)}%)` }}
          >
            {events.map((event, index) => (
              <div className="carousel-item" key={index}>
                <img src={event.image} alt={`Event ${index + 1}`} />
              </div>
            ))}
          </div>
        </div>
        <button onClick={handleNext} className="carousel-button">›</button>
      </div>
    </div>
  );
}

export default Events;
